const { name } = require('../../config/pkg.json');

module.exports = function isDiscordInvite(message) {
    if (!message) throw new Error(`[${name}: MISSING_PARAMETER] The parameter 'message' is required to provide.`);

    if (typeof message !== 'string') throw new TypeError(`[${name}: INVALID_TYPE] 'message' parameter is not a String.`);

    let regex = /discord(?:\.com|app\.com|\.gg)[\/invite\/]?(?:[a-zA-Z0-9\-]{2,32})/g;

    const result = regex.test(message);

    return result;
};