const { name } = require('../../config/pkg.json');

module.exports = function RandomString(length) {
    if (!length) throw new Error(`[${name}: MISSING_PARAMETER] The parameter 'length' is required to provide.`);

	if (typeof length !== 'number') throw new TypeError(`[${name}: INVALID_TYPE] 'length' parameter is not a Number.`);

    if (length < 1) throw new Error(`[${name}: OPTION_ERROR] 'length' cannot be negative or 0.`);

    var result = '';
    var characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    var charactersLength = characters.length;

    for (var i = 0; i < length; i++) {
        result += characters.charAt(Math.floor(Math.random() *
            charactersLength));
    };

    return result.toString();
}