const { name } = require('../../config/pkg.json');

module.exports = function Wait(time) {
	if (!time) throw new Error(`[${name}: MISSING_PARAMETER] The parameter 'time' is required to provide.`);

	if (typeof time !== 'number') throw new TypeError(`[${name}: INVALID_TYPE] 'time' parameter is not a Number.`);

	return new Promise((resolve) => setTimeout(resolve, time));
};