const mongoose = require("mongoose");

module.exports = mongoose.model("NoteSchemaSuperDJS",
    new mongoose.Schema(
        {
            user: {
                type: String,
                required: true
            },
            guild: {
                type: String,
                required: true
            },
            author: {
                type: String,
                required: true
            },
            content: {
                type: String,
                required: true
            },
            edited: {
                type: Boolean,
                required: false
            }
        }
    )
);